function bind_key_statistics()
{


	$(document).keydown(function(e) {
	    switch(e.which) 
	    {
	        case 37: // left
	        	change_day_prew()
	        break;

	        case 38: // up
	        break;

	        case 39: // right
	        	change_day_next()
	        break;

	        case 40: // down
	        break;

	        default: return; // exit this handler for other keys
	    }
	    e.preventDefault(); // prevent the default action (scroll / move caret)
	});
	
}





   
	
