function update_tx_station(lat,lon,idn,tg,label,active)
{

	var update_icon = vectorSource.getFeatureById("stn_"+idn);

	if(update_icon!= null )
	{

    	if(tg_colours[tg] == null)
    	{
    		tg_colours[tg]= new Array();
    		tg_colours[tg]["id"] =tg;
    		tg_colours[tg]["color"] =random_css_colour();
    		tg_colours[tg]["TXT"] ="";
    	}
    	var geometry =update_icon.getGeometry()
    	var style = update_icon.getStyle();
    	var html = "stn_"+idn;
//    	console.log(style);
    	var colour = tg_colours[tg]['color'];
    
    
    	
    	html=html.replace(" ","_");
    	var canvas = document.createElement('canvas');
    	var ctx = canvas.getContext('2d');
    	canvas.setAttribute('width', 60);
    	canvas.setAttribute('height', 100);
    	canvas.setAttribute('id', 'icon_'+html);
    
    
    	ctx.beginPath();
    
    	
    	
    	ctx.arc(30, 30, 25, 25, Math.PI * 2, true); // Outer circle
    
    
    	
    	ctx.moveTo(5, 35);
    	ctx.lineTo(30, 80);
    	ctx.lineTo(55, 35);
    	ctx.lineWidth = 3;
    	ctx.stroke();
    	ctx.fillStyle =colour;
    	ctx.fill();
    	ctx.beginPath();
    	ctx.arc(30, 30, 10, 0, Math.PI * 2, true); // Outer circle
    	if(active == false)
    		ctx.fillStyle ="#FFFFFF";
    	else
    	{
    		ctx.fillStyle ="#FF4500";
    		ctx.lineWidth = 1;
    		ctx.stroke();
    	}
    	
    	ctx.fill();
    	ctx.beginPath();
    	ctx.scale(-0.5, -0.5);
    
    

    	icon_save[idn] = canvas.toDataURL();
    	
    
    	iconFeature = new ol.Feature({
    	    geometry: new ol.geom.Point(new ol.proj.transform([lon,lat], 'EPSG:4326', 'EPSG:3857')),
    	});
    	var lablel_text="";
    	var newZoom = map.getView().getZoom();

    	if(newZoom >= 7)
    	{
    		lablel_text = label;
    	}
    	
    
    	var iconStyle = new ol.style.Style({
    	        image: new ol.style.Icon(({
    	        anchor: [0.5, 1.0],
    	        anchorXUnits: 'fraction',
    	        anchorYUnits: 'fraction',
    	        opacity: 0.9,
    	        src: icon_save[idn],
    			scale:0.5
    	        })),
    	        text: new ol.style.Text({
    	                     font: '12px helvetica,sans-serif',
    	                     text: lablel_text,
    	                     fontSize: 12,
    						 scale:1.2,
    	                     fill: new ol.style.Fill({
    	                         color: '#000'
    	                     }),
    	                     stroke: new ol.style.Stroke({
    	                         color: '#fff',
    	                         width: 2
    	                     })
    	                  })
    	});
    
    	update_icon.setStyle(iconStyle);
	}
	
}


